import React, {useState, useEffect} from 'react';
import "../App.css"
import {Link, useParams} from "react-router-dom";
import {Button} from 'react-bootstrap';



function Priziv() {
    const params = useParams();
    const prizivId = params.id

    const [priziv, setPriziv] = useState([]);

    useEffect(() => {
        fetch(`http://127.0.0.1:8000/prizivniki/`)
            .then(response => response.json())
            .then((result) => {
                setPriziv(result);
            })
    }, []);


    const arm=(quantity, name, surname, vozrast, categoriya)=> {
        const ob = {
            quantity: quantity,
            name: name,
            surname: surname,
            vozrast: vozrast,
            categoriya: categoriya
        }
        fetch("http://127.0.0.1:8000/cart/", {
            method: "post",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(ob)
        })
            .then(res => res.json())
            .then(res => {
                console.log(res);
            })
    }

    const del = (pk) => {
        fetch('http://127.0.0.1:8000/prizivniki/' + pk + '/', {
            method: 'DELETE',
        })
            .then(res => {
                if (res.ok) {
                    console.log("HTTP request successful");
                    window.location.reload();
                } else {
                    console.log("HTTP request unsuccessful");
                }
            })
    }

    return (
        <>
        <div>
            <Link to="/priziv">Призывники</Link>
            <div className={"assortment"}><h1>Призывники</h1></div>
            <a href="/priziv/cart" className="arm4_button">Список для отправки в армию</a><br/>
            <div className={"priziv_list"}>
                {priziv.map(item => (
                    <div className={"priziv"} key={"surname:" + item.surname}>
                        <Link to={`/prizivniki/${item.pk}/`}>{item.surname} {item.name}<br/></Link>
                        <a>Наличие отсрочки: {item.otsrochka}</a>
                        <input id="buy_button" className={`arm_button ${localStorage.getItem('theme')}`}
                               type="submit" value="В армию" onClick={()=>{arm(1 , item.name, item.surname, item.vozrast, item.categoriya, prizivId)}}/>
                        <input id="buy_button" className="arm_button" type="submit" value="Удалить из военкомата" onClick={()=>{del(item.pk)}}/>
                        <br/>
                    </div>
                ))}
            </div>
        </div>
        {/* <Modal show={show} onHide={handleClose}>*/}
        {/*    <Modal.Header closeButton>*/}
        {/*        <Modal.Title>Корзина</Modal.Title>*/}
        {/*    </Modal.Header>*/}
        {/*    <Modal.Body>*/}
        {/*        <Form>*/}
        {/*            <Form.Group controlId="fromBasicEmail">*/}
        {/*                <Form.Label><ul>*/}
        {/*                    {basket.map(el =>*/}
        {/*                        <Form.Group >*/}
        {/*                            <Form.Label>Название книги: {el.name}</Form.Label>*/}
        {/*                            <br/><Form.Label>Количество страниц: {el.pages}</Form.Label>*/}
        {/*                            <br/><Button value={el.id} onClick ={*/}
        {/*                            ()=> {*/}
        {/*                                dispatch(decrem(el.pages));*/}
        {/*                                setBasket(basket.filter(item => item.id !== el.id))*/}
        {/*                            }}>Удалить</Button>*/}
        {/*                        </Form.Group>)}*/}
        {/*                </ul></Form.Label>*/}
        {/*                <br/><Form.Label>Страниц к печати: <Status sum = {state.totalPages}/></Form.Label>*/}
        {/*            </Form.Group>*/}
        {/*        </Form>*/}
        {/*    </Modal.Body>*/}
        {/*</Modal>*/}
        </>
    );
}

export default Priziv;
