import React, {useState, useEffect} from 'react';
import "./App.css"
import {
    BrowserRouter,
    Switch,
    Route,
    Link,
    useParams,
    useRouteMatch
} from "react-router-dom";
import {Button} from 'react-bootstrap';



function Priziv() {

    const [basket, setBasket] = useState([]);

    useEffect(() => {
        fetch(`http://127.0.0.1:8000/prizivniki/`)
            .then(response => response.json())
            .then((result) => {
                setPriziv(result);
            })
    }, []);

    const [priziv, setPriziv] = useState([]);

    return (
        <>
        <div>
            <Link to="/priziv">Призывники</Link>
            <div className={"assortment"}><h1>Призывники</h1><br/></div>
            <div className={"priziv_list"}>

                {priziv.map(item => (
                    <div className={"priziv"} key={"surname:" + item.surname}>
                        <Link to={`/prizivniki/${item.pk}/`}>{item.surname} {item.name}<br/></Link>
                        <a>Наличие отсрочки: {item.otsrochka}<br/><br/></a>
                        <br/><Button onClick ={
                            async ()=> {
                                setBasket([...basket,item]);
                                console.log(basket);
                            }}>В армию</Button>
                    </div>
                ))}
            </div>
        </div>
        <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
                <Modal.Title>Корзина</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group controlId="fromBasicEmail">
                        <Form.Label><ul>
                            {basket.map(el =>
                                <Form.Group >
                                    <Form.Label>Название книги: {el.name}</Form.Label>
                                    <br/><Form.Label>Количество страниц: {el.pages}</Form.Label>
                                    <br/><Button value={el.id} onClick ={
                                    ()=> {
                                        dispatch(decrem(el.pages));
                                        setBasket(basket.filter(item => item.id !== el.id))
                                    }}>Удалить</Button>
                                </Form.Group>)}
                        </ul></Form.Label>
                        <br/><Form.Label>Страниц к печати: <Status sum = {state.totalPages}/></Form.Label>
                    </Form.Group>
                </Form>
            </Modal.Body>
        </Modal>
        </>
    );
}
export default Priziv;
